contracts/MockPYUSD.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.28;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

contract MockPYUSD is ERC20 {
    uint8 private _decimals;
    bool public transferFailing;
    mapping(address => bool) public forceFailOnTransfer;

    constructor() ERC20("PayPal USD", "PYUSD") {
        _decimals = 6;
        _mint(msg.sender, 1000000 * 10**_decimals); // 1m pyusd for testing
    }

    function decimals() public view virtual override returns (uint8) {
        return _decimals;
    }

    function mint(address to, uint256 amount) external {
        _mint(to, amount);
    }

    function burn(address from, uint256 amount) external {
        _burn(from, amount);
    }

    function setBalance(address account, uint256 amount) external {
        uint256 currentBalance = balanceOf(account);
        if (amount > currentBalance) {
            _mint(account, amount - currentBalance);
        } else if (amount < currentBalance) {
            _burn(account, currentBalance - amount);
        }
    }

    function setTransferFailing(bool failing) external {
        transferFailing = failing;
    }

    function setForceFailOnTransfer(address account, bool fail) external {
        forceFailOnTransfer[account] = fail;
    }

    function transfer(address to, uint256 amount) public override returns (bool) {
        if (transferFailing || forceFailOnTransfer[msg.sender]) {
            return false; // simulate transfer failures for testing
        }
        return super.transfer(to, amount);
    }

    function transferFrom(address from, address to, uint256 amount) public override returns (bool) {
        if (transferFailing || forceFailOnTransfer[from]) {
            return false;
        }
        return super.transferFrom(from, to, amount);
    }
}

contracts/RelayerRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.28;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract RelayerRegistry is ReentrancyGuard, Ownable {
    using SafeERC20 for IERC20;
    struct Relayer {
        uint256 stakedAmount;
        uint256 successfulExecutions;
        uint256 failedExecutions;
        uint256 totalFeesEarned;
        uint256 withdrawalRequestTime;
        bool isActive;
        bool withdrawalRequested;
    }

    mapping(address => Relayer) public relayers;
    uint256 public constant MINIMUM_STAKE = 1000e6; // 1000 pyusd
    uint256 public constant SLASH_AMOUNT = 100e6;
    uint256 public constant CONSECUTIVE_FAILURES_THRESHOLD = 3;
    uint256 public constant SLASHING_COOLDOWN = 1 days;

    uint256 public slashAmountConfig = SLASH_AMOUNT;
    uint256 public failureThresholdConfig = CONSECUTIVE_FAILURES_THRESHOLD;

    mapping(address => uint256) public lastSlashTime;
    mapping(address => uint256) public consecutiveFailures;
    mapping(address => bool) public isSlashed;

    uint256 public constant WITHDRAWAL_DELAY = 7 days;
    address public immutable PYUSD_ADDRESS;
    address public subscriptionManager;

    event RelayerRegistered(
        address indexed relayer,
        uint256 stakedAmount
    );

    event RelayerUnregistered(
        address indexed relayer,
        uint256 returnedStake
    );

    event WithdrawalRequested(
        address indexed relayer,
        uint256 requestTime
    );

    event ExecutionRecorded(
        address indexed relayer,
        bool success,
        uint256 feeAmount
    );

    event RelayerSlashed(
        address indexed relayer,
        uint256 slashAmount,
        uint256 remainingStake
    );

    event RelayerRestaked(
        address indexed relayer,
        uint256 amount,
        uint256 newStake
    );

    event SlashingParametersUpdated(
        uint256 slashAmount,
        uint256 failureThreshold
    );

    event EmergencySlash(
        address indexed relayer,
        uint256 amount,
        string reason
    );

    modifier onlySubscriptionManager() {
        require(msg.sender == subscriptionManager, "Only SubscriptionManager");
        _;
    }

    constructor(address _pyusdAddress) Ownable(msg.sender) {
        require(_pyusdAddress != address(0), "Invalid PYUSD address");
        PYUSD_ADDRESS = _pyusdAddress;
    }

    function setSubscriptionManager(address _subscriptionManager) external onlyOwner {
        require(_subscriptionManager != address(0), "Invalid address");
        require(subscriptionManager == address(0), "Already set");
        subscriptionManager = _subscriptionManager;
    }

    function registerRelayer(uint256 stakeAmount) external nonReentrant {
        require(relayers[msg.sender].stakedAmount == 0, "Relayer already registered");
        require(stakeAmount >= MINIMUM_STAKE, "Insufficient stake amount");

        // stake tokens to become active relayer
        IERC20 token = IERC20(PYUSD_ADDRESS);
        token.safeTransferFrom(msg.sender, address(this), stakeAmount);

        relayers[msg.sender] = Relayer({
            stakedAmount: stakeAmount,
            successfulExecutions: 0,
            failedExecutions: 0,
            totalFeesEarned: 0,
            isActive: true,
            withdrawalRequestTime: 0,
            withdrawalRequested: false
        });
        consecutiveFailures[msg.sender] = 0;
        isSlashed[msg.sender] = false;
        lastSlashTime[msg.sender] = 0;

        emit RelayerRegistered(msg.sender, stakeAmount);
    }

    function requestWithdrawal() external {
        Relayer storage relayer = relayers[msg.sender];
        require(relayer.isActive, "Relayer not active");
        require(relayer.stakedAmount > 0, "Relayer not registered");
        require(!relayer.withdrawalRequested, "Withdrawal already requested");

        relayer.withdrawalRequested = true;
        relayer.withdrawalRequestTime = block.timestamp;

        emit WithdrawalRequested(msg.sender, block.timestamp);
    }

    function unregisterRelayer() external nonReentrant {
        Relayer storage relayer = relayers[msg.sender];
        require(relayer.isActive, "Relayer not active");
        require(relayer.stakedAmount > 0, "Relayer not registered");
        require(relayer.withdrawalRequested, "Must request withdrawal first");
        require(
            block.timestamp >= relayer.withdrawalRequestTime + WITHDRAWAL_DELAY,
            "Withdrawal delay not met"
        );
        require(!isSlashed[msg.sender], "Relayer slashed");


        uint256 stakeToReturn = relayer.stakedAmount;

        // clear relayer state before transfer
        relayer.isActive = false;
        relayer.stakedAmount = 0;
        relayer.withdrawalRequested = false;
        relayer.withdrawalRequestTime = 0;

        IERC20(PYUSD_ADDRESS).safeTransfer(msg.sender, stakeToReturn);

        emit RelayerUnregistered(msg.sender, stakeToReturn);

        delete relayers[msg.sender];
        delete consecutiveFailures[msg.sender];
        delete lastSlashTime[msg.sender];
        delete isSlashed[msg.sender];
    }

    function recordExecution(
        address relayerAddress,
        bool success,
        uint256 feeAmount
    ) external onlySubscriptionManager {
        Relayer storage relayer = relayers[relayerAddress];
        require(relayer.stakedAmount > 0, "Relayer not registered");
        require(relayer.isActive, "Relayer not active");
        require(!isSlashed[relayerAddress], "Relayer slashed");

        // track relayer performance and earnings
        if (success) {
            relayer.successfulExecutions++;
            relayer.totalFeesEarned += feeAmount;
            consecutiveFailures[relayerAddress] = 0;
        } else {
            relayer.failedExecutions++;
            consecutiveFailures[relayerAddress]++;
            if (consecutiveFailures[relayerAddress] >= failureThresholdConfig) {
                _slashRelayer(relayerAddress, slashAmountConfig);
            }
        }

        emit ExecutionRecorded(relayerAddress, success, feeAmount);
    }

    function isRelayerActive(address relayerAddress) external view returns (bool) {
        return relayers[relayerAddress].isActive;
    }

    function getRelayerStats(address relayerAddress) external view returns (
        uint256 stakedAmount,
        uint256 successfulExecutions,
        uint256 failedExecutions,
        uint256 totalFeesEarned,
        bool isActive
    ) {
        Relayer storage relayer = relayers[relayerAddress];
        return (
            relayer.stakedAmount,
            relayer.successfulExecutions,
            relayer.failedExecutions,
            relayer.totalFeesEarned,
            relayer.isActive
        );
    }

    function getRelayerInfo(address relayerAddress) external view returns (Relayer memory) {
        return relayers[relayerAddress];
    }

    function canExecute(address relayerAddress) external view returns (bool) {
        Relayer storage relayer = relayers[relayerAddress];
        return relayer.isActive && !isSlashed[relayerAddress] && relayer.stakedAmount >= MINIMUM_STAKE;
    }

    function getConsecutiveFailures(address relayerAddress) external view returns (uint256) {
        return consecutiveFailures[relayerAddress];
    }

    function getTimeUntilSlashCooldown(address relayerAddress) external view returns (uint256) {
        if (block.timestamp >= lastSlashTime[relayerAddress] + SLASHING_COOLDOWN) {
            return 0;
        }
        return (lastSlashTime[relayerAddress] + SLASHING_COOLDOWN) - block.timestamp;
    }

    function restakeAfterSlash(uint256 amount) external nonReentrant {
        require(isSlashed[msg.sender], "Relayer not slashed");
        require(amount > 0, "Invalid amount");
        require(relayers[msg.sender].stakedAmount > 0, "Relayer not registered");

        IERC20(PYUSD_ADDRESS).safeTransferFrom(msg.sender, address(this), amount);

        Relayer storage relayer = relayers[msg.sender];
        relayer.stakedAmount += amount;

        if (relayer.stakedAmount >= MINIMUM_STAKE) {
            relayer.isActive = true;
            isSlashed[msg.sender] = false;
        }

        emit RelayerRestaked(msg.sender, amount, relayer.stakedAmount);
    }

    function emergencySlash(address relayerAddress, uint256 amount, string calldata reason) external onlyOwner {
        require(amount > 0, "Invalid slash amount");
        Relayer storage relayer = relayers[relayerAddress];
        require(relayer.stakedAmount >= amount, "Insufficient stake");

        require(relayer.stakedAmount > 0, "Relayer not registered");

        _applySlash(relayerAddress, amount);

        emit EmergencySlash(relayerAddress, amount, reason);
    }

    function emergencyUnslash(address relayerAddress) external onlyOwner {
        require(isSlashed[relayerAddress], "Relayer not slashed");

        isSlashed[relayerAddress] = false;
        consecutiveFailures[relayerAddress] = 0;

        Relayer storage relayer = relayers[relayerAddress];
        if (relayer.stakedAmount >= MINIMUM_STAKE) {
            relayer.isActive = true;
        }
    }

    function updateSlashingParameters(uint256 slashAmount, uint256 failureThreshold) external onlyOwner {
        require(slashAmount > 0, "Invalid slash amount");
        require(failureThreshold > 0, "Invalid threshold");

        slashAmountConfig = slashAmount;
        failureThresholdConfig = failureThreshold;

        emit SlashingParametersUpdated(slashAmount, failureThreshold);
    }

    function _slashRelayer(address relayerAddress, uint256 amount) internal {
        Relayer storage relayer = relayers[relayerAddress];
        require(relayer.stakedAmount >= amount, "Insufficient stake to slash");
        require(block.timestamp >= lastSlashTime[relayerAddress] + SLASHING_COOLDOWN, "Slash cooldown active");

        _applySlash(relayerAddress, amount);
    }

    function _applySlash(address relayerAddress, uint256 amount) internal {
        Relayer storage relayer = relayers[relayerAddress];
        require(relayer.stakedAmount >= amount, "Insufficient stake");

        lastSlashTime[relayerAddress] = block.timestamp;

        relayer.stakedAmount -= amount;
        isSlashed[relayerAddress] = true;
        consecutiveFailures[relayerAddress] = 0;

        if (relayer.stakedAmount < MINIMUM_STAKE) {
            relayer.isActive = false;
        }

        emit RelayerSlashed(relayerAddress, amount, relayer.stakedAmount);
    }
}

contracts/SubscriptionManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.28;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "./RelayerRegistry.sol";

contract SubscriptionManager is ReentrancyGuard {
    enum SubscriptionStatus {
        ACTIVE,
        PAUSED,
        CANCELLED,
        EXPIRED,
        COMPLETED
    }

    struct Subscription {
        address subscriber;
        address merchant;
        uint256 amount;
        uint256 interval;
        uint256 startTime;
        uint256 maxPayments;
        uint256 maxTotalAmount;
        uint256 expiry;
        uint256 nonce;
        SubscriptionStatus status;
    }

    struct SubscriptionIntent {
        address subscriber;
        address merchant;
        uint256 amount;
        uint256 interval;
        uint256 startTime;
        uint256 maxPayments;
        uint256 maxTotalAmount;
        uint256 expiry;
        uint256 nonce;
    }

    mapping(bytes32 => Subscription) public subscriptions;
    mapping(bytes32 => uint256) public executedPayments;
    mapping(address => uint256) public currentNonce;

    address public immutable PYUSD_ADDRESS;
    RelayerRegistry public immutable RELAYER_REGISTRY;
    uint256 public constant PROTOCOL_FEE_BPS = 50; // 0.5% protocol fee

    bytes32 private immutable DOMAIN_SEPARATOR;
    // eip-712 typehashes for signature verification
    bytes32 public constant SUBSCRIPTION_INTENT_TYPEHASH = keccak256(
        "SubscriptionIntent(address subscriber,address merchant,uint256 amount,uint256 interval,uint256 startTime,uint256 maxPayments,uint256 maxTotalAmount,uint256 expiry,uint256 nonce)"
    );
    bytes32 public constant PAUSE_REQUEST_TYPEHASH = keccak256(
        "PauseRequest(bytes32 subscriptionId,uint256 nonce)"
    );
    bytes32 public constant RESUME_REQUEST_TYPEHASH = keccak256(
        "ResumeRequest(bytes32 subscriptionId,uint256 nonce)"
    );

    event SubscriptionCreated(
        bytes32 indexed subscriptionId,
        address indexed subscriber,
        address indexed merchant,
        uint256 amount,
        uint256 interval,
        uint256 maxPayments,
        uint256 maxTotalAmount,
        uint256 expiry
    );

    event SubscriptionPaused(
        bytes32 indexed subscriptionId,
        address indexed subscriber
    );

    event SubscriptionResumed(
        bytes32 indexed subscriptionId,
        address indexed subscriber
    );

    event SubscriptionCancelled(
        bytes32 indexed subscriptionId,
        address indexed subscriber,
        address indexed merchant
    );

    event PaymentExecuted(
        bytes32 indexed subscriptionId,
        address indexed subscriber,
        address indexed merchant,
        uint256 paymentNumber,
        uint256 amount,
        uint256 fee,
        address relayer
    );

    event PaymentFailed(
        bytes32 indexed subscriptionId,
        address indexed subscriber,
        address indexed merchant,
        uint256 amount,
        string reason
    );

    constructor(address _pyusdAddress, address _relayerRegistry) {
        require(_pyusdAddress != address(0), "Invalid PYUSD address");
        require(_relayerRegistry != address(0), "Invalid relayer registry address");

        PYUSD_ADDRESS = _pyusdAddress;
        RELAYER_REGISTRY = RelayerRegistry(_relayerRegistry);

        // eip-712 domain separator for signature verification
        DOMAIN_SEPARATOR = keccak256(
            abi.encode(
                keccak256("EIP712Domain(string name,string version,uint256 chainId,address verifyingContract)"),
                keccak256(bytes("Aurum")),
                keccak256(bytes("1")),
                block.chainid,
                address(this)
            )
        );
    }

    function verifyIntent(
        SubscriptionIntent calldata intent,
        bytes calldata signature
    ) public view returns (bool valid, address signer) {
        bytes32 structHash = _hashIntent(intent);
        bytes32 digest = keccak256(
            abi.encodePacked("\x19\x01", _domainSeparatorV4(), structHash)
        );

        address recovered = ECDSA.recover(digest, signature);
        return (recovered == intent.subscriber, recovered);
    }

    function _hashIntent(
        SubscriptionIntent memory intent
    ) internal pure returns (bytes32) {
        return keccak256(
            abi.encode(
                SUBSCRIPTION_INTENT_TYPEHASH,
                intent.subscriber,
                intent.merchant,
                intent.amount,
                intent.interval,
                intent.startTime,
                intent.maxPayments,
                intent.maxTotalAmount,
                intent.expiry,
                intent.nonce
            )
        );
    }

    function _domainSeparatorV4() internal view returns (bytes32) {
        return DOMAIN_SEPARATOR;
    }

    function getNextNonce(address _subscriber) external view returns (uint256) {
        return currentNonce[_subscriber];
    }

    function createSubscription(
        SubscriptionIntent calldata intent,
        bytes calldata signature
    ) external returns (bytes32 subscriptionId) {
        require(intent.nonce == currentNonce[intent.subscriber], "Invalid nonce");

        (bool valid, address signer) = verifyIntent(intent, signature);
        require(valid && signer == intent.subscriber, "Invalid signature");

        _validateSubscriptionParams(intent);

        // unique subscription id from intent hash + signature
        subscriptionId = keccak256(abi.encodePacked(
            _hashIntent(intent),
            signature
        ));

        require(subscriptions[subscriptionId].subscriber == address(0), "Subscription already exists");

        uint256 requiredAllowance = intent.amount * intent.maxPayments;
        require(
            IERC20(PYUSD_ADDRESS).allowance(intent.subscriber, address(this)) >= requiredAllowance,
            "Insufficient PYUSD allowance"
        );
        subscriptions[subscriptionId] = Subscription({
            subscriber: intent.subscriber,
            merchant: intent.merchant,
            amount: intent.amount,
            interval: intent.interval,
            startTime: intent.startTime,
            maxPayments: intent.maxPayments,
            maxTotalAmount: intent.maxTotalAmount,
            expiry: intent.expiry,
            nonce: intent.nonce,
            status: SubscriptionStatus.ACTIVE
        });

        currentNonce[intent.subscriber]++; // prevent replay attacks

        emit SubscriptionCreated(
            subscriptionId,
            intent.subscriber,
            intent.merchant,
            intent.amount,
            intent.interval,
            intent.maxPayments,
            intent.maxTotalAmount,
            intent.expiry
        );

        return subscriptionId;
    }

    function executeSubscription(
        bytes32 subscriptionId,
        address relayer
    ) external nonReentrant {
        require(relayer == msg.sender, "Relayer mismatch");
        require(RELAYER_REGISTRY.canExecute(msg.sender), "Relayer not authorized");

        Subscription storage subscription = subscriptions[subscriptionId];

        require(subscription.subscriber != address(0), "Subscription does not exist");
        require(subscription.status == SubscriptionStatus.ACTIVE, "Subscription not active");

        // handle expired subscriptions
        if (block.timestamp > subscription.expiry) {
            subscription.status = SubscriptionStatus.EXPIRED;
            emit PaymentFailed(
                subscriptionId,
                subscription.subscriber,
                subscription.merchant,
                subscription.amount,
                "Subscription expired"
            );

            RELAYER_REGISTRY.recordExecution(msg.sender, false, 0);
            return;
        }

        require(_isPaymentDue(subscriptionId), "Payment not due yet");

        uint256 currentPaymentNumber = executedPayments[subscriptionId] + 1;

        uint256 fee = (subscription.amount * PROTOCOL_FEE_BPS) / 10000;

        if (!_canExecutePayment(subscriptionId)) {
            emit PaymentFailed(
                subscriptionId,
                subscription.subscriber,
                subscription.merchant,
                subscription.amount,
                "Execution constraints not met"
            );
            RELAYER_REGISTRY.recordExecution(msg.sender, false, 0);
            return;
        }
        uint256 merchantAmount = subscription.amount - fee;

        (bool paymentSuccess, string memory failureReason) = _attemptPayment(subscription, merchantAmount, fee);

        if (paymentSuccess) {
            executedPayments[subscriptionId]++;

            emit PaymentExecuted(
                subscriptionId,
                subscription.subscriber,
                subscription.merchant,
                currentPaymentNumber,
                subscription.amount,
                fee,
                msg.sender
            );

            RELAYER_REGISTRY.recordExecution(msg.sender, true, fee);
            // mark as completed if max payments reached
            if (executedPayments[subscriptionId] >= subscription.maxPayments) {
                subscription.status = SubscriptionStatus.COMPLETED;
            }
        } else {
            if (bytes(failureReason).length == 0) {
                failureReason = "Payment execution failed";
            }
            emit PaymentFailed(
                subscriptionId,
                subscription.subscriber,
                subscription.merchant,
                subscription.amount,
                failureReason
            );
            RELAYER_REGISTRY.recordExecution(msg.sender, false, 0);
        }
    }

    function pauseSubscription(
        bytes32 subscriptionId,
        bytes calldata signature
    ) external {
        Subscription storage subscription = subscriptions[subscriptionId];

        require(subscription.subscriber != address(0), "Subscription does not exist");
        require(subscription.status == SubscriptionStatus.ACTIVE, "Subscription not active");

        bytes32 structHash = keccak256(
            abi.encode(
                PAUSE_REQUEST_TYPEHASH,
                subscriptionId,
                currentNonce[subscription.subscriber]
            )
        );
        bytes32 digest = keccak256(
            abi.encodePacked("\x19\x01", _domainSeparatorV4(), structHash)
        );
        address signer = ECDSA.recover(digest, signature);
        require(signer == subscription.subscriber, "Invalid signature");

        currentNonce[subscription.subscriber]++;

        subscription.status = SubscriptionStatus.PAUSED;

        emit SubscriptionPaused(subscriptionId, subscription.subscriber);
    }

    function resumeSubscription(
        bytes32 subscriptionId,
        bytes calldata signature
    ) external {
        Subscription storage subscription = subscriptions[subscriptionId];

        require(subscription.subscriber != address(0), "Subscription does not exist");
        require(subscription.status == SubscriptionStatus.PAUSED, "Subscription not paused");

        bytes32 structHash = keccak256(
            abi.encode(
                RESUME_REQUEST_TYPEHASH,
                subscriptionId,
                currentNonce[subscription.subscriber]
            )
        );
        bytes32 digest = keccak256(
            abi.encodePacked("\x19\x01", _domainSeparatorV4(), structHash)
        );
        address signer = ECDSA.recover(digest, signature);
        require(signer == subscription.subscriber, "Invalid signature");

        currentNonce[subscription.subscriber]++;

        subscription.status = SubscriptionStatus.ACTIVE;

        emit SubscriptionResumed(subscriptionId, subscription.subscriber);
    }

    function cancelSubscription(bytes32 subscriptionId) external {
        Subscription storage subscription = subscriptions[subscriptionId];

        require(subscription.subscriber != address(0), "Subscription does not exist");
        require(msg.sender == subscription.subscriber, "Only subscriber can cancel");
        require(
            subscription.status == SubscriptionStatus.ACTIVE || 
            subscription.status == SubscriptionStatus.PAUSED,
            "Subscription cannot be cancelled"
        );

        subscription.status = SubscriptionStatus.CANCELLED;

        emit SubscriptionCancelled(subscriptionId, subscription.subscriber, subscription.merchant);
    }

    function getSubscription(bytes32 subscriptionId) external view returns (Subscription memory) {
        return subscriptions[subscriptionId];
    }

    function getPaymentCount(bytes32 subscriptionId) external view returns (uint256) {
        return executedPayments[subscriptionId];
    }

    function getNextPaymentTime(bytes32 subscriptionId) external view returns (uint256) {
        Subscription storage subscription = subscriptions[subscriptionId];
        require(subscription.subscriber != address(0), "Subscription does not exist");

        uint256 paymentsExecuted = executedPayments[subscriptionId];
        return subscription.startTime + (paymentsExecuted * subscription.interval);
    }

    function isSubscriptionActive(bytes32 subscriptionId) external view returns (bool) {
        Subscription storage subscription = subscriptions[subscriptionId];
        return subscription.subscriber != address(0) && subscription.status == SubscriptionStatus.ACTIVE;
    }

    function _isPaymentDue(bytes32 subscriptionId) internal view returns (bool) {
        Subscription storage subscription = subscriptions[subscriptionId];
        uint256 paymentsExecuted = executedPayments[subscriptionId];
        uint256 nextPaymentTime = subscription.startTime + (paymentsExecuted * subscription.interval);

        return block.timestamp >= nextPaymentTime;
    }

    function _canExecutePayment(bytes32 subscriptionId) internal view returns (bool) {
        Subscription storage subscription = subscriptions[subscriptionId];
        uint256 paymentsExecuted = executedPayments[subscriptionId];

        if (paymentsExecuted >= subscription.maxPayments) {
            return false;
        }

        uint256 totalPaidSoFar = paymentsExecuted * subscription.amount;
        if (totalPaidSoFar + subscription.amount > subscription.maxTotalAmount) {
            return false;
        }

        IERC20 pyusd = IERC20(PYUSD_ADDRESS);
        uint256 totalRequired = subscription.amount;

        if (pyusd.balanceOf(subscription.subscriber) < totalRequired) {
            return false;
        }

        if (pyusd.allowance(subscription.subscriber, address(this)) < totalRequired) {
            return false;
        }

        return true;
    }

    function _validateSubscriptionParams(SubscriptionIntent calldata intent) internal view {
        require(intent.expiry > block.timestamp, "Subscription intent expired");
        require(intent.startTime <= intent.expiry, "Start time must be before expiry");
        require(intent.amount > 0, "Amount must be greater than zero");
        require(intent.interval > 0, "Interval must be greater than zero");
        require(intent.maxPayments > 0, "Max payments must be greater than zero");
        require(intent.maxTotalAmount >= intent.amount * intent.maxPayments, "Max total amount insufficient");
    }

    function _attemptPayment(
        Subscription storage subscription,
        uint256 merchantAmount,
        uint256 fee
    ) internal returns (bool success, string memory failureReason) {
        IERC20 pyusd = IERC20(PYUSD_ADDRESS);

        uint256 totalRequired = subscription.amount;
        if (pyusd.balanceOf(subscription.subscriber) < totalRequired) {
            return (false, "Insufficient balance for payment");
        }

        if (pyusd.allowance(subscription.subscriber, address(this)) < totalRequired) {
            return (false, "Insufficient allowance for payment");
        }

        bytes memory transferData = abi.encodeWithSelector(
            IERC20.transferFrom.selector,
            subscription.subscriber,
            subscription.merchant,
            merchantAmount
        );

        bool merchantTransferSuccess = _callToken(address(pyusd), transferData);
        if (!merchantTransferSuccess) {
            return (false, "Merchant transfer failed");
        }

        bytes memory feeTransferData = abi.encodeWithSelector(
            IERC20.transferFrom.selector,
            subscription.subscriber,
            msg.sender,
            fee
        );

        bool feeTransferSuccess = _callToken(address(pyusd), feeTransferData);
        if (!feeTransferSuccess) {
            return (false, "Fee transfer failed");
        }

        return (true, "");
    }

    function _callToken(address token, bytes memory data) internal returns (bool) {
        (bool success, bytes memory returndata) = token.call(data);
        if (!success) {
            return false;
        }

        if (returndata.length == 0) {
            return true;
        }

        return abi.decode(returndata, (bool));
    }
}

